//注意这里
var data= arg.returnValue;
if(data.error!=null){
	 var dataSet=view.get("#dsErrorMessageVo");
     dataSet.setData(data.error);
	 view.get("#progressLabel").set("text","导入失败！");
     view.get("#errorProductMsgDialog").show();
