AutoForm.ClientEvent:
var nowdays = new Date();
    var year = nowdays.getFullYear();
    var month = nowdays.getMonth();
    if(month==0)
    {
        month=12;
        year=year-1;
    }
    if (month < 10) {
        month = "0" + month;
    }
    var firstDay = year + "-" + month + "-" + "01 00:00:00";//上个月的第一天
    var myDate = new Date(year, month, 0);
    var lastDay = year + "-" + month + "-" + myDate.getDate()+" 23:59:59";//上个月的最后一天
	view.get("#queryConditionFrm.entity").set({
		startTime:firstDay,
		endTime:lastDay
	});

DataColumn.ClientEvent:
var adjustPalletNum=arg.data.get("adjustPalletNum");
var palletNum=arg.data.get("palletNum");
if(isNullOrEmpty(adjustPalletNum)){
	arg.dom.style.background = "#fcc5c5";
	arg.dom.style.color = "blue";
}
if(!isNullOrEmpty(adjustPalletNum) && !isNullOrEmpty(palletNum)){
	if(adjustPalletNum!=palletNum){
		arg.dom.style.background = "#fcc5c5";
		arg.dom.style.color = "blue";
	}
}

arg.processDefault = true;

Button.ClientEvent:
view.get("#updateAction").execute(
				function(result){
					if(result.code == "SUCCESS"){
						view.get("#updateDialog").hide();
						dorado.MessageBox.alert("修改成功");
						var data = view.get("#dsCondition.data").current;
						with (view.get("#dsQuotaList")){
											set("parameter", data);
											flushAsync();
										}
					}else{
						dorado.MessageBox.alert(result.data);
					}
				}
			);

