View.ClientEvent:
var customerid = GetQueryString("customerId");
var startTime = GetQueryString("createTime");
var endTime = GetQueryString("createEndTime");
var subjectCode = GetQueryString("subjectCode");
var isCalculated = GetQueryString("isCalculated");

if(customerid == undefined || customerid == null){
	return;
}

var data = view.get("#dsCondition.data").current;
data.set("customerid",customerid);
data.set("startTime",startTime);
data.set("endTime",endTime);
data.set("subjectCode",subjectCode);
data.set("isCalculated",isCalculated);

var dsQuotaList= view.get("#dsQuotaList");
with (dsQuotaList) {
	dsQuotaList.clear();
	set("parameter", data);
	flush();
} 


function GetQueryString(name){
    var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)"); 
    var r = window.location.search.substr(1).match(reg);
    if(r!=null)return  unescape(r[2]); return null;
}

