var retryAction = view.get("#RetryAction");
var datas = view.get("#dataSetCondition.data").current;

var merchantId=datas.get("customerId");
if(merchantId=='' || merchantId==undefined){
	dorado.MessageBox.alert("商家不能为空!");
	return;
}

validRetryAction.set("parameter",datas);
retryAction.set("parameter",datas);
validRetryAction.execute(function(retMap){
	if(retMap.key == "Billed"){
		dorado.MessageBox.alert(retMap.value);
		return;
	}
	if(retMap.key == "Error"){
		dorado.MessageBox.alert(retMap.value);
		return;
	}
	if(retMap.key == "Calculated" || retMap.key == "OK"){
		dorado.MessageBox.confirm(retMap.value+" 是否确定重算?",function(){
			retryAction.execute(function(result){
				dorado.MessageBox.alert(result);
				with (view.get("#dataSetTrunkWayBill")){
					flushAsync();
				}
			});
		});
		return;
	}
});

