AutoForm.ClientEvent:
var nowdays = new Date();
var year = nowdays.getFullYear();
var month = nowdays.getMonth();
if(month==0)
{
    month=12;
    year=year-1;
}
if (month < 10) {
    month = "0" + month;
}
var firstDay = year + "-" + month + "-" + "01 00:00:00";//上个月的第一天
var myDate = new Date(year, month, 0);
var lastDay = year + "-" + month + "-" + myDate.getDate()+" 23:59:59";//上个月的最后一天
view.get("#queryWayBillForm.entity").set({
	startTime:firstDay,
	endTime:lastDay
});

Button.ClientEvent:
var entity = view.get("#billEditForm").get("entity");	

var adjustWeight = entity.get("adjustWeight");

var bizEntity = view.get("#dataSetTrunkWayBill.data");
var totalWeight = bizEntity.current.get("totalWeight");

if(undefined==adjustWeight || adjustWeight=='' && adjustWeight!='0'){
	dorado.MessageBox.alert("请填写调整重量!");
	return;
}

if (isNaN(adjustWeight)) {
	dorado.MessageBox.alert("调整重量必须为数字!");
	return;
}

var sum = totalWeight + adjustWeight;
//dorado.MessageBox.alert(sum);
if(isNaN(sum) || sum <=0){
	dorado.MessageBox.alert("调整重量后,重量将小于或等于0,禁止调整!");
	return;
}

view.id("updateAjustWeightAction").execute(
	function(result){
		with (view.get("#dataSetTrunkWayBill")){
				//定义查询条件json结构s
				flushAsync();
			}
		view.id("dialogAjustWeight").hide();
		dorado.MessageBox.alert(result);
	}
);

dorado.MessageBox.confirm("是否确定取消此操作？",
	function(){
		view.id("dataSetTrunkWayBill").getData("#").cancel();
		view.id("dialogAjustWeight").hide();
	}
);

view.id("updateBillAction").execute(
	function(result){
		with (view.get("#dataSetTrunkWayBill")){
				//定义查询条件json结构s
				flushAsync();
		}
		view.id("dialogBillEdit").hide();
		dorado.MessageBox.alert(result);
	}
);

dorado.MessageBox.confirm("是否确定取消此操作？",
	function(){
		view.id("dataSetTrunkWayBill").getData("#").cancel();
		view.id("dialogBillEdit").hide();
	}
);

