ToolBarButton.ClientEvent:
var entity=view.get("#autoForm1.entity");
var map={
	dgroupid:entity.dgroupid,
	dgroupname:entity.dgroupname
}
 view.get("#dsDatagroupEntity").set("parameter",map).flushAsync();

var selectData=view.get("#dataGrid1").get("selection");//得到的数据是一个array对象
if(selectData!=null && selectData!=''){
	//view.get("#dsTmsCarrierEntity.data").current=selectData;
	//var datas = view.get("#dsTmsDo.data");
	view.get("#dsDatagroupEntity.data").setCurrent(selectData);
	view.get("#updateDataGroupDialog").show();
}else{
	dorado.MessageBox.alert("请选择一行数据！");
	return;
}

var selectData=view.get("#dataGrid1").get("selection");//得到的数据是一个array对象
//dorado.MessageBox.alert(selectData);
if(selectData!=null && selectData!=''){
	//view.get("#dsTmsCarrierEntity.data").current=selectData;
	//var datas = view.get("#dsTmsDo.data");
	view.get("#dsDatagroupEntity.data").setCurrent(selectData);
	view.get("#deleteGroup").execute(function(result){
		if(result==1){
			dorado.MessageBox.alert("禁止删除!此数据组还有相关联数据");
		}else{
			dorado.MessageBox.alert("删除成功!");
			view.get("#dsDatagroupEntity.data").remove();
		}
	});
}else{
	dorado.MessageBox.alert("请选择一行数据！");
	return;
}

var selectData=view.get("#dataGrid1").get("selection");//得到的数据是一个array对象
if(selectData!=null && selectData!=''){
	view.get("#dsDatagroupEntity.data").setCurrent(selectData);
	var data=view.get("#dsDatagroupEntity").getData("#");//当前选择的数据
	var json={
	dgroupid:data.get("dgroupid")
	}
	view.get("#dsDatagroupCustomerEntity1").set("parameter",json).flushAsync();//数据组商家
	view.get("#dsDatagroupCkEntity1").set("parameter",json).flushAsync();//数据组仓库
	view.get("#dsDatagroupUserEntity1").set("parameter",json).flushAsync();//数据组用户
	
	//添加数据组id数据
	view.set("#autoFormData.entity",{
		datagroupid:data.get("dgroupid")
	});
	view.set("#autoFormData1.entity",{
		datagroupid:data.get("dgroupid")
	});
	view.set("#autoFormData2.entity",{
		datagroupid:data.get("dgroupid")
	});
	
	//设置查询条件为空
	view.get("#autoFromCustomer").set("entity",{});
	view.get("#autoFromCk").set("entity",{});
	view.get("#autoFormUser").set("entity",{});
	
	
	//var list=view.get("#dsDatagroupCustomerEntity1").getData("");
	//商家，仓库，账号
	var map={
	msg:1,//从后台查
	dgroupid:data.get("dgroupid")
	}
   view.get("#dsDatagroupCustomerEntity2").set("parameter",map).flushAsync();
   view.get("#dsDatagroupCkEntity2").set("parameter",map).flushAsync();
   view.get("#dsDatagroupUserEntity2").set("parameter",map).flushAsync();

}else{
	dorado.MessageBox.alert("请选择一行数据！");
	return;
}

