Button.ClientEvent:
var datagroupid=view.get("#autoFormData.entity").datagroupid;

var selectData=view.get("#dataGrid3").get("selection");//得到的数据是一个array对象
//dorado.MessageBox.alert(selectData[0])
if(selectData!=null && selectData!=''&&datagroupid!=null&&datagroupid!=''){
	for(var i=0;i<selectData.length;i++){
		//dorado.MessageBox.alert(selectData[i].get("customerid"));
		var json={
			customerid:selectData[i].get("customerid"),
			customername:selectData[i].get("shortname"),
			dgroupid:datagroupid
		}
		view.get("#dsDatagroupCustomerEntity1").insert(json);
		view.get("#dsDatagroupCustomerEntity2.data").each(function(item){
			if(selectData[i].get("customerid") == item.get("customerid")){
				item.remove();
			}
		});
	}
}else{
	dorado.MessageBox.alert("至少选择一行要增加的商家数据且数据组不能为空！");
	return;
}

//dorado.MessageBox.alert(customer);
var selectData=view.get("#dataGrid2").get("selection");//得到的数据是一个array对象
if(selectData!=null && selectData!=''){
	for(var i=0;i<selectData.length;i++){
		//dorado.MessageBox.alert(selectData[i].get("customerid"));
		var json={
			customerid:selectData[i].get("customerid"),
			shortname:selectData[i].get("customername")
		}
		view.get("#dsDatagroupCustomerEntity2").insert(json);
		view.get("#dsDatagroupCustomerEntity1.data").each(function(item){
			if(selectData[i].get("customerid") == item.get("customerid")){
				item.remove();
			}
		});
	}
}else{
	dorado.MessageBox.alert("请选择要移除的数据！");
	return;
}

var entity=view.get("#autoFromCustomer.entity");
var datagroupid=view.get("#autoFormData.entity").datagroupid;
var list=view.get("#dsDatagroupCustomerEntity2").getData("");
if(datagroupid!=null&&datagroupid!=''){
	var customerid=entity.customerid;
    var selectedItems=[]; //定义一个数组
    list.each(function(item){
        if (item.get("customerid")==customerid){
            selectedItems.push(item);
        }
    });
    view.set("#dataGrid3.selection",selectedItems);
}else{
	dorado.MessageBox.alert("请选择一行数据组！");
	return;
}

