if(datagroupid!=null&&datagroupid!=''){
	var userid=userEntity.userid;
	var selectedItems=[]; //定义一个数组
	list.each(function(entity){
	    if (entity.get("userid")==userid){
	        selectedItems.push(entity);
	    }
	});
	view.set("#dataUser.selection",selectedItems);
