View.ClientEvent:
var date=new Date;  
var year=date.getFullYear();  
var month=date.getMonth();
if(month==0){
	month=12;
	year=year-1;
}
view.get("#queryForm.entity").set("createYear",year);
view.get("#queryForm.entity").set("createMonth",month);

ToolBarButton.ClientEvent:
var data = view.get("#dsCondition.data").current;
with (view.get("#dsCustomerCalStatus")){
	set("parameter", data);
	flushAsync();
}

//清空子表
view.get("#dsCustomerSubjectStatus.data").clear();

var cancelAction = view.id("cancelAction");
var data = view.get("#dsCustomerCalStatus.data").current;
var query = {
	customerId:data.get("customerId")
}
dorado.MessageBox.confirm("是否取消订购？", function(){
	cancelAction.set("parameter", query).execute();
	
	//刷新主表
	var data = view.get("#dsCondition.data").current;
	with (view.get("#dsCustomerCalStatus")){
		set("parameter", data);
		flushAsync();
	}
	//清空子表
	view.get("#dsCustomerSubjectStatus.data").clear();
})

DataColumn.ClientEvent:
var customerStatus=arg.data.get("customerStatus");
if(!isNullOrEmpty(customerStatus)){
	arg.dom.style.color = "black";
	if(customerStatus=="2"){
		arg.dom.style.color = "red";
	}else if(customerStatus=="1"){
		arg.dom.style.color = "green";
	}else{
		arg.dom.style.color = "black";
	}	
}

arg.processDefault = true;

var dataDetail = view.get("#dsCustomerSubjectStatus.data").current;
jQuery(arg.dom).empty();
jQuery(arg.dom).xCreate({
	tagName: "A",
	href: "#",
	content:dataDetail.get("uncalcuCount")+"",
	onclick:function(){
		//数据字典查询科目对应界面的URL
		var subjectUrlAction = view.get("#subjectUrlAction");
		//主的与明细的当前行
		var dataMain = view.get("#dsCustomerCalStatus.data").current;
		var dataDetail = view.get("#dsCustomerSubjectStatus.data").current;
		
		//时间转换,获取指定月份最后一天
		var creMonth = dataMain.get("creMonth");
		var year = creMonth.toString().substring(0,4);
		var month = creMonth.toString().substring(4,6);
		var createTime = year+"-"+month+"-"+"01 00:00:00";
		var dateYar = getCurrentMonthLast(year+"-"+month);
		var createEndTime=dateYar.replace(new RegExp('/','g'),"-") + " 23:59:59";
		
		//参数拼接(未计算，计算状态：99)
		var param="?customerId="+dataMain.get("customerId")+"&createTime="+createTime+"&createEndTime="+createEndTime+"&subjectCode="+dataDetail.get("subjectCode")+"&isCalculated=99";
		var data = {
			code:dataDetail.get("subjectCode")
		};
		//商品存储费单独判断，其余的费用科目走数据字典配置
		if("wh_product_storage" == dataDetail.get("subjectCode") && "item" == dataDetail.get("feesType")){
			window.top.openUrlInFrameTab("com.jiuyescm.bms.biz.storage.view.productsRepertory.d"+param, "商品库存_件", "url(>skin>common/icons.gif) -280px -20px", true);
		}else if("wh_product_storage" == dataDetail.get("subjectCode") && "pallet" == dataDetail.get("feesType")){
			window.top.openUrlInFrameTab("com.jiuyescm.bms.biz.pallet.view.BizPalletInfo.d.d"+param, "托数管理", "url(>skin>common/icons.gif) -20px -100px", true);
		}else{
			subjectUrlAction.set("parameter",data).execute(function(result){
				window.top.openUrlInFrameTab(result['url']+param, result['name'], result['icon'], true);
			});
		}
		
		//日期转换
		function getCurrentMonthLast(date){
			var endDate = new Date(date); //date 是需要传递的时间如：2018-08
			var month=endDate.getMonth();
			var nextMonth=++month;
			var nextMonthFirstDay=new Date(endDate.getFullYear(),nextMonth,1);
			var oneDay=1000*60*60*24;
			var dateString=new Date(nextMonthFirstDay-oneDay);
			return dateString.toLocaleDateString(); //返回 2018/8/31
		}
		var end=dateYar.replace(new RegExp('/','g'),"-"); //把2018/8/31 替换成2018-8-31
	}
});

