DataGrid.ClientEvent:
var data = view.get("#dsCustomerCalStatus").getData();

with (view.get("#dsCustomerSubjectStatus")){
	//定义查询条件json结构
	var queryJson={
		creMonth:data.current.get("creMonth"),
		customerId:data.current.get("customerId")
	};
	set("parameter", queryJson);
	flushAsync();
}

DataColumn.ClientEvent:
var customerStatus=arg.data.get("customerStatus");
if(!isNullOrEmpty(customerStatus)){
	if(customerStatus=="2"){
		arg.dom.style.color = "red";
	}else if(customerStatus=="1"){
		arg.dom.style.color = "green";
	}else{
		arg.dom.style.color = "black";
	}			
}

arg.processDefault = true;

FloatPanel.ClientEvent:
var data = this.get("#viewSelectCustomer.userData");
if (data != null) {
	var entity = this.get("#dsCondition").getData("#");

    entity.set({
        customerId:data.get("customerid"),
		customerName:data.get("customername")
    });
}

ToolBarButton.ClientEvent:
var data = view.get("#dsCustomerLookup.data").current;
var action = this.get("#saveAction");
var query = {
	"customerId":data.get("customerid")
}

action.set("parameter", query).execute(function(result){
	dorado.MessageBox.alert(result);
	view.id("customerOrder").close();
	var datas = view.get("#dsCondition.data").current;
	with (view.get("#dsCustomerCalStatus")){
		set("parameter", datas);
		flushAsync();
	}
});

