AutoForm.ClientEvent:
var date = new Date();
var seperator1 = "-";
var seperator2 = ":";
var month = date.getMonth() + 1;
var strDate = date.getDate();
if (month >= 1 && month <= 9) {
    month = "0" + month;
}
if (strDate >= 0 && strDate <= 9) {
    strDate = "0" + strDate;
}

var lw = new Date(date - 1000*60*60*24*30);
var lwmonth = lw.getMonth() + 1;
var lwstrDate = lw.getDate();
if (lwmonth >= 1 && lwmonth <= 9) {
    lwmonth = "0" + lwmonth;
}
if (lwstrDate >= 0 && lwstrDate <= 9) {
    lwstrDate = "0" + lwstrDate;
}
var startdate = lw.getFullYear() + seperator1 + lwmonth + seperator1 + lwstrDate
	+ " " + "00" + seperator2 + "00" + seperator2 + "00";

var enddate = date.getFullYear() + seperator1 + month + seperator1 + strDate
	+ " " + "23" + seperator2 + "59" + seperator2 + "59";

view.get("#dsFeesPayBillCondition").getData().current.set("startTime",startdate)
view.get("#dsFeesPayBillCondition").getData().current.set("endTime",enddate)

