var OperationCellRenderer = $extend(dorado.widget.grid.SubControlCellRenderer,
{
    createSubControl : function(arg) {
        if (arg.data.rowType)
            return null;
        //返回按钮对象并为按钮对象绑定onClick事件
        return new dorado.widget.Link({
            onClick : function(self) {
				view.get("#impDialog").show();
            }
        });
    },
    refreshSubControl : function(Link, arg) {
		Link.set({
		    text :'导入对账单',
			href:"#"
		});
    }
});

self.set("#operator.renderer", new OperationCellRenderer());

