var data=view.get("#dsFeesPayBill.data").current;
if(!data){
	dorado.MessageBox.alert("请先选择账单");
	return;
}
if("1" == data.get("status")){
	dorado.MessageBox.alert("该账单已核销,不允许操作");
	return;
}
var deliveryid = data.get("deliveryid");
var billstarttime = data.get("startTime");
var billendtime = data.get("endTime");
var seperator = "-";
if(isNullOrEmpty(deliveryid)){
	dorado.MessageBox.alert("请选择宅配商");
	return;
}
if(isNullOrEmpty(billendtime)){
	dorado.MessageBox.alert("结束日期不能为空");
	return;
}
if(!isNullOrEmpty(billstarttime)){
	if(billstarttime.getTime()>billendtime.getTime()){
		dorado.MessageBox.alert("开始日期不能大于结束日期！");
		return;
	}
	//格式化开始日期
	billstarttime = billstarttime.getFullYear() + seperator + 
		formatMonth(billstarttime.getMonth()+1) + seperator +
		formatDay(billstarttime.getDate());
	data.set("startTime", billstarttime);
	billstarttime = data.get("startTime");
}
//格式化结束日期
billendtime= billendtime.getFullYear() + seperator + 
	formatMonth(billendtime.getMonth()+1) + seperator +
	formatDay(billendtime.getDate());
data.set("endTime", billendtime);
billendtime = data.get("endTime");
//配送
with (view.get("#dsCustomBillDispatch")){
	var queryJson={
		deliveryid:deliveryid,
		operatorTimeBegin:billstarttime,
		operatorTimeEnd:billendtime
	};
	set("parameter", queryJson);
	flushAsync();
}
	
view.get("#addDispatchBillDialog").show();

