//格式化月份
function formatMonth(month){
	if(month==0) {
        month=12;
        year=year-1;
    }
	if (month >= 1 && month <= 9) {
        month = "0" + month;
    }
	return month;
}
//格式化天
function formatDay(strDate){
	if (strDate >= 0 && strDate <= 9) {
        strDate = "0" + strDate;
    }
	return strDate;
}

