var action=view.get("#delBillAction");
dorado.MessageBox.confirm("确定从账单["+billNo+"]剔除费用编号为["+feesDispatchData.get("feesNo")+"]的记录?",function(){
	action.set("parameter",param).execute(function(){
		dorado.MessageBox.alert("剔除账单成功!");
		//刷新账单
		var condition = view.get("#dsFeesPayBillCondition.data").current;
		condition.set("billNo", billNo);
		with (view.get("#dsFeesPayBill")){
			set("parameter", condition);
			flush();
		}
		//刷新统计
		with (view.get("#dsDispatchWareHouseBill")){
			set("parameter", data.get("billno")+data.get("deliveryid"));
			flushAsync();
		}
		var disDatas=view.get("#dsDispatchWareHouseBill.data");
		var disamount=0
		disDatas.each(function(item){
			disamount+=parseFloat(item.get("amount"));
		});
		view.id("dispatchTab").set("caption","配送("+disamount.toFixed(2)+"元)");
	});
})

