RadioGroup.ClientEvent:
var currentValue= self._value;
var data=view.get("#dsFeesPayBill.data").current;

var seperator = "-";
if(currentValue == 0){
	//上周
	var weekFirstDay = getDays()[0];//本周的第一天
	var preWeekFirstDay = getPreviousWeekDatas(weekFirstDay)[0];//上个周第一天
	var preWeekLastDay =  getPreviousWeekDatas(weekFirstDay)[6];//上个周最后一天
	var firstDay = preWeekFirstDay.getFullYear() + seperator + formatMonth(preWeekFirstDay.getMonth()+1) + seperator + formatDay(preWeekFirstDay.getDate());
	var lastDay = preWeekLastDay.getFullYear() + seperator + formatMonth(preWeekLastDay.getMonth()+1) + seperator + formatDay(preWeekLastDay.getDate());
	if(lastDay){
		view.get("#dsFeesPayBill").getData().current.set("startTime",firstDay);
		view.get("#dsFeesPayBill").getData().current.set("endTime",lastDay);
	}
}else if(currentValue == 1){
	//上月
	var date=new Date();
	var year = date.getFullYear();
	var month = formatMonth(date.getMonth());
	var firstDay = year + seperator + month + seperator + "01";//上个月的第一天

	var lastDate = new Date(year, month, 0);
	var lastDay = year + seperator + month + seperator + lastDate.getDate();//上个月的最后一天
	if(lastDay){
		view.get("#dsFeesPayBill").getData().current.set("startTime",firstDay);
		view.get("#dsFeesPayBill").getData().current.set("endTime",lastDay);
	}
}

//取得当前日期一周内的某一天
function getWeek(i) {
	var now = new Date();
	var n = now.getDay();
	var start = new Date();
	start.setDate(now.getDate() - n + i);//取得一周内的第一天、第二天、第三天...
	return start;
}
//取得当前日期一周内的七天
function getDays() {
	var days = new Array();
	for(var i=1;i<=7;i++) {
		days[i-1] = getWeek(i);
	}
	return days;
}
//取得上一周的日期数(共七天)
function getPreviousWeekDatas(ndt) {
	var days = new Array();
	for(var i=-7;i<=-1;i++) {
		var dt = new Date(ndt);
		days[7+i] = getPreviousWeek(dt,i);
	}
	return days;
}

//指定日期的上一周(前七天)
function getPreviousWeek(dt,i) {
	dt.setDate(dt.getDate()+i);
	return dt;
}

//格式化月份
function formatMonth(month){
	if(month==0) {
        month=12;
        year=year-1;
    }
	if (month >= 1 && month <= 9) {
        month = "0" + month;
    }
	return month;
}
//格式化天
function formatDay(strDate){
	if (strDate >= 0 && strDate <= 9) {
        strDate = "0" + strDate;
    }
	return strDate;
}

