	var deliveryid = data.get("deliveryid");
	var deliverSelectlist=data.get("deliverSelectlist");
	var billstarttime = data.get("startTime");
	var billendtime = data.get("endTime");
	if(isNullOrEmpty(billname)){
		view.get("#billRange").set("value",0);
		dorado.MessageBox.alert("账单名称不能为空");
		return;
	}
	if(isNullOrEmpty(deliveryid)){
		view.get("#billRange").set("value",0);
		dorado.MessageBox.alert("请选择宅配商");
		return;
	}
	if(isNullOrEmpty(billendtime)){
		view.get("#billRange").set("value",0);
		dorado.MessageBox.alert("结束日期不能为空");
		return;
	}
	if(!isNullOrEmpty(billstarttime)){
		if(billstarttime.getTime()>billendtime.getTime()){
			view.get("#billRange").set("value",0);
			dorado.MessageBox.alert("开始日期不能大于结束日期！");
			return;
		}
	}
	
	var parameter={
		startTime:billstarttime,
		endTime:billendtime,
		deliverSelectlist:deliverSelectlist
	}
	var action=view.get("#ajaxcountDispatchBillAction");
	//var parameter=JSON.stringify(data);
	action.set("parameter",parameter).execute(function(result){
		
		if(result!="succ"){
			dorado.MessageBox.confirm("生成提货费错误!是否继续？",function(){
				refesh();
				view.get("#customBillDialog").show();
			});
		}else{
			refesh();
			view.get("#customBillDialog").show();
		}
	});

}
