function refesh(){
	with (view.get("#dsCustomBillDispatch")){
		var queryJson={
			deliverSelectlist:deliverSelectlist,
			deliveryid:deliveryid,
			operatorTimeBegin:billstarttime,
			operatorTimeEnd:billendtime
		};
		set("parameter", queryJson);
		flushAsync();
	}
	//异常
	with (view.get("#dsCustomBillAbnormal")){
		var queryJson={
			deliverSelectlist:deliverSelectlist,
			deliveryid:deliveryid,
			operatorStartTime:billstarttime,
			operatorEndTime:billendtime
		};
		set("parameter", queryJson);
		flushAsync();
	}
}

FloatPanel.ClientEvent:
var tabledata = this.get("#viewSelectCarrier2.userData");
var carrierid="";
if (tabledata != null&&tabledata.length>0) {
	var index=0;
	var flag=true;
	var deliverid="";
	var deliverSelectlist="";
	var deliverSet=view.get("#dsSelectDeliver");
	deliverSet.clear();
	var parentCompany="";
	tabledata.each(function(item){
		index++;
		if(parentCompany==""){
			parentCompany=item.get("parentCompany");
		}else{
			if(item.get("parentCompany")!=parentCompany){
				flag=false;
			}
		}
		if(index==tabledata.length){
			deliverid+=item.get("deliverid");
			deliverSelectlist+=item.get("deliverid")+"&"+item.get("delivername");
		}else{
			deliverid+=item.get("deliverid")+",";
			deliverSelectlist+=item.get("deliverid")+"&"+item.get("delivername")+",";
		}
		deliverSet.insert(
		{
			deliverid:item.get("deliverid"),
			delivername:item.get("delivername"),
			delivercode:item.get("delivercode")
		});
	});
	if(!flag){
		deliverSet.clear();
		alert("只能选择一个总公司生成账单！");
		return;
	}
	var currentData=view.get("#dsFeesPayBill.data").current;
	currentData.set("deliveryid",deliverid);
	currentData.set("deliverSelectlist",deliverSelectlist);
	
	var ajaxaction=view.get("#ajaxgettimeAction");
	ajaxaction.set("parameter",deliverid).execute(function(result){
		if(result==null||result.length==0){		
			view.id("infoContainer").set("content","宅配商【"+ deliverid +"】还未生成账单");
		}else{
			var info="";
			result.each(function(rs){
				info+="宅配商【"+rs.deliverName+"】最后生成时间<span style='color:red;'>"+rs.endTime.replace("T"," ").replace("Z","")+"</span><br/>";
			});
			//alert(info);
			view.id("infoContainer").set("content",info);
		}
	});
}

