		var abnormalDatas=view.get("#dsAbnormalWareHouseBill.data");
		var abnormalamount=0;
		abnormalDatas.each(function(item){
			abnormalamount+=parseFloat(item.get("amount"));
		});
		view.id("abnormalTab").set("caption","异常("+abnormalamount.toFixed(2)+"元)");
	});
})

