	var billendtime = data.get("endTime");
	if(!billendtime){
		dorado.MessageBox.alert("结束日期不能为空");
		return;
	}
	if(!deliveryid){
		dorado.MessageBox.alert("请选择宅配商");
		return;
	}
	var waybillNo = condition.get("waybillNo");
	var feesNo = condition.get("feesNo");
	with (view.get("#dsCustomBillAbnormal")){
		//定义查询条件json结构
		var queryJson={
			deliveryid:deliveryid,
			operatorStartTime:billstarttime,
			operatorEndTime:billendtime,
			expressnum:waybillNo,
			feeNo:feesNo
		};
		set("parameter", queryJson);
		flushAsync();
	}
}else{
	dorado.MessageBox.alert("账单信息为空");
	return;
}

