ToolBarButton.ClientEvent:
var data =view.get("#dsFeesReceiveQuery.data");//获取条件查询数据
var currentData = data.current;
var cre=currentData.get("crestime");
var end=currentData.get("creetime");

if(!isNullOrEmpty(cre) && !isNullOrEmpty(end)){
 	if(cre.getTime()>end.getTime()){
		dorado.MessageBox.alert("起始时间不能大于结束时间！");
		return;
	}
}

var m=getDays(cre,end);
if(m>31){
	dorado.MessageBox.alert("不能查询时间间隔大于1个月的数据!");
	return;
}

with (view.get("#dsFeesReceiveDeliver")){
	set("parameter", data);
	flushAsync();
}


//计算日期之差
function getDays(strDateStart,strDateEnd){
  var iDays = parseInt(Math.abs(strDateEnd - strDateStart ) / 1000 / 60 / 60 /24)//把相差的毫秒数转换为天数
   return iDays ;
}

AutoForm.ClientEvent:
var nowdays = new Date();
    var year = nowdays.getFullYear();
    var month = nowdays.getMonth();
    if(month==0)
    {
        month=12;
        year=year-1;
    }
    if (month < 10) {
        month = "0" + month;
    }
    var firstDay = year + "-" + month + "-" + "01 00:00:00";//上个月的第一天
    var myDate = new Date(year, month, 0);
    var lastDay = year + "-" + month + "-" + myDate.getDate()+" 23:59:59";//上个月的最后一天
	view.get("#queryForm.entity").set({
		crestime:firstDay,
		creetime:lastDay
	});

