Button.ClientEvent:
var data=view.get("#dsFeesReceiveStorageEntity.data").current;
var customerId=data.get("customerId");
var costSubject=data.get("otherSubjectCode");
var cost=data.get("cost");

if(costSubject==null||costSubject==""||costSubject=="全部"||costSubject==undefined){
	dorado.MessageBox.alert("请选择费用科目");
	return ;
}

var ajaxaction=view.get("#ajaxgetpriceAction");
ajaxaction.set("parameter",{
	customerId:customerId,
	subjectCode:costSubject
}).execute(function(result){
	if(result==""){
		$("#d_unitPrice input").val("");
		data.set({
		unitPrice:''
		});
		dorado.MessageBox.alert("未能获取到相关单价信息");
	}else{
		if(cost==null||cost==""||cost==undefined){
	       dorado.MessageBox.alert("请填写金额值");
	          return false;
         }

		view.id("updateAction").execute(
                        function(){
                            view.id("dialogEdit").hide();
							view.get("#dsFeesReceiveStorageEntity").flushAsync();
                        }
                    );
	}
});

dorado.MessageBox.confirm("是否确定取消此操作？",
                        function(){
                            view.id("dsFeesReceiveStorageEntity").getData("#").cancel();
                            view.id("dialogEdit").hide();
                        }
                    );

AutoFormElement.ClientEvent:
var entity = this.get("#dsFeesReceiveStorageEntity").getData("#");

var quantity = entity.get("quantity");
if(quantity==null||quantity==''){
	quantity = 0;
}
var unitPrice = entity.get("unitPrice");
if(unitPrice==null||unitPrice==''){
	unitPrice = 0;
}

entity.set({
    cost: parseFloat(quantity)*parseFloat(unitPrice).toFixed(4)
});

