	dorado.MessageBox.alert("请选择费用科目");
	return ;
}
var ajaxaction=view.get("#ajaxgetpriceAction");
ajaxaction.set("parameter",{
	customerId:customerId,
	subjectCode:costSubject
}).execute(function(result){
	if(result==""){
		$("#d_unitPrice input").val("");
		data.set({
		unitPrice:''
		});
		dorado.MessageBox.alert("未能获取到相关单价信息");
	}else{
		data.set({
		unitPrice:result
		});
		$("#d_unitPrice input").val(result);
	}
});

