	//判断该合同是否已失效
	var nowTime = new Date(); 
	
	//获取失效日期
	var expireDate=data.current.get("expireDate");
	
	var oDate2 = new Date(expireDate);
	//已过期的合同不能修改
	if(nowTime.getTime() > oDate2.getTime()){
 		dorado.MessageBox.alert("该合同已过期，无法编辑!");
		return;
