						if(entity.get("code")==dataCode){
						//alert("进来");
						entity.set("isCheck",true);
			   			entity.set("templateId",datatemplate);		
						}
						
					}else{
						if(entity.get("codeName")=='运输增值费'){
							entity.set("isCheck",true);
			   				entity.set("templateId",datatemplate);	
						}
					}
				
			}
			
		 }
