				view.get("#msgAutoForm").getElement("msgElement").set("text",ruleInstruction);
				view.get("#msgAutoForm").getElement("lblRuleNo").set("text","规则编号: "+ruleNo);
		       	view.get("#ruleDialog").show();
	    	}
		}
	);
}

