Button.ClientEvent:
var entity = view.id("formDetailEdit").get("entity");
var count = 0;

if(entity.get("startTime") >= entity.get("endTime")){
	dorado.MessageBox.alert("结束时间必须大于开始时间");
	return;
}

if(parseFloat(entity.get("upLimit")) <= parseFloat(entity.get("downLimit"))){
	dorado.MessageBox.alert("上限必须大于下限");
	return;
}
if(entity.get("firstPrice") != undefined && entity.get("firstPrice") != ""){
	count++;
}
if(entity.get("firstPriceRate") != undefined && entity.get("firstPriceRate") != ""){
	count++;
}
if(entity.get("continuePrice") != undefined && entity.get("continuePrice") != ""){
	count++;
}
if(entity.get("continuePirceRate") != undefined && entity.get("continuePirceRate") != ""){
	count++;
}
if(entity.get("unitPrice") != undefined && entity.get("unitPrice") != ""){
	count++;
}
if(entity.get("unitPriceRate") != undefined && entity.get("unitPriceRate") != ""){
	count++;
}
if(count == 0){
	dorado.MessageBox.alert("折扣首价、首价折扣率、折扣续价、续价折扣率、折扣一口价、一口价折扣率不能全为空");
	return;
}
if(count > 1){
	dorado.MessageBox.alert("折扣首价、首价折扣率、折扣续价、续价折扣率、折扣一口价、一口价折扣率有且只能有一个有值");
	return;
}
view.id("updateDetailAction").execute(
                        function(result){
							if(result["fail"]){
								dorado.MessageBox.alert(result["fail"]);
								return;
							}else{
								//刷新
								var data = view.get("#dsBmsQuoteDiscountTemplateEntity").getData();
								with (view.get("#dsBmsQuoteDiscountDetailEntity")){
									//定义查询条件json结构
									var queryJson={
										templateCode:data.current.get("templateCode"),
									};
									set("parameter", queryJson);
									flushAsync();
								}
								view.id("dialogEditDetail").hide();
							}                    
                        }
                    );

dorado.MessageBox.confirm("是否确定取消此操作？",
                        function(){
                            view.id("dsBmsQuoteDiscountDetailEntity").getData("#").cancel();
                            view.id("dialogEditDetail").hide();
                        }
                    );

