DataSetDropDown.ClientEvent:
var entity = view.id("queryForm").get("entity");
var bizType = entity.get("bizType");

if(bizType != undefined){
	var subDropDown = view.get("#subjectCodeDataSetDropDown");
	subDropDown.set("dataSet","dsSubjectCode");
	var parameter={
		bizTypeCode:bizType
	};
	view.get("#dsSubjectCode").set("parameter",parameter).flushAsync();
}else{
	//this.get("#dsSubjectCode").clear();
	dorado.MessageBox.alert("请先选择费用类型");
	return;
}

var entity = view.id("formEdit").get("entity");
var bizType = entity.get("bizType");
if(bizType != undefined){
	var subDropDown = view.get("#subjectCodeEditDataSetDropDown");
	subDropDown.set("dataSet","dsSubjectCode");
	var parameter={
		bizTypeCode:bizType
	};
	view.get("#dsSubjectCode").set("parameter",parameter).flushAsync();
}else{
	this.get("#dsSubjectCode").clear();
	dorado.MessageBox.alert("请先选择费用类型");
	return;
}

Trigger.ClientEvent:
var correctAction = view.get("#ajaxSystemCodeAction");
var datas = view.get("#dsBmsQuoteDiscountTemplateEntity.data").current;
correctAction.set("parameter",datas);
correctAction.execute(function(result){
	if(result!=null){	
		var subview = view.get("#viewSelectServiceType").get("subView");
		//清空
		var subviewData=subview.get("#dsPubCarrierServicetypeEntity.data");
		subviewData.clear();
		var ds = subview.get("#dsPubCarrierServicetypeEntityQuery.data").current;
		ds.set("carrierid",result);
		//直接调用方法查询
		var tmpDataSet=subview.get("#dsPubCarrierServicetypeEntity");
		tmpDataSet.set("parameter", ds).flushAsync();
		view.get("#panelServiceType").show();
	}
});

FloatPanel.ClientEvent:
var data = this.get("#viewSelectServiceType.userData");
if (data != null) {
	var entity = this.get("#dsBmsQuoteDiscountDetailEntity").getData("#");
