var currentData = datas.current;
if(currentData){
	dorado.MessageBox.confirm("是否确定要删除此条记录?",function(){
		view.get("#mobanRemoveAction").execute(
			function(result){
				alertMsg(result.code, result.message);
				
				with (view.get("#dsGenericTemplate")){
					flushAsync();
				}
			}
		);
	});
}else{
	alertMsg("FAIL", "请首先选择要删除的记录!");
}

