	if(!isNullOrEmpty(currentData.get("templateType")) && "S" == currentData.get("templateType")){
		alertMsg("FAIL", "标准模版不允许复制!");
		return;
	}
	dorado.MessageBox.confirm("是否确定要复制此条模板?",function(){
		view.get("#mobanCopyAction").execute({
