if(undefined==startWarehouseId || startWarehouseId==''){
	dorado.MessageBox.alert("请选择始发仓!");
	return;
}

if(undefined==provinceName || provinceName==''){
	dorado.MessageBox.alert("请选择省名!");
	return;
}


