		view.id("dsPriceMainDistribution").getData("#").cancel();
		view.id("dialogEdit").hide();
	}
);

var entity=view.get("#dsBizDispatchBillEntity").getData().current;
//获取报价
var dataPrice = view.get("#dsPriceMainDistribution.data").current;

var jsonObj=JSON.parse(JSON.stringify(dataPrice));

//获取模板
var dsGenericTemplate= view.get("#dsGenericTemplate.data").current;
entity.set("warehouseCode",dataPrice.get("startWarehouseId"));
entity.set("receiveProvinceId",dataPrice.get("provinceId"));
entity.set("receiveCityId",dataPrice.get("cityId"));
entity.set("receiveDistrictId",dataPrice.get("areaId"));
entity.set("customerid",dsGenericTemplate.get("customerId"));
entity.set("carrierId",dsGenericTemplate.get("deliver"));
entity.set("priceList",jsonObj);

view.get("#calculateAction").execute(
	function(result){
		if(result.code == "SUCCESS"){
			dorado.MessageBox.alert(result.data);		
		}else{
			dorado.MessageBox.alert(result);
		}
	}
);

