$("#d_progressLabel").show();
$("#d_exportProgressBar").show();
view.get("#progressLabel").set("text", "请不要关闭当前页面，系统正在处理数据，请耐心等待...");
view.get("#exportProgressBar").set("value", 0);
var action = view.get("#getProgess");
var setAction = view.get("#setProgess");
var oldVal = 0;
var getProgress = function() {
	action.execute(function (res) {
		
		if (oldVal != res) {
			for (oldVal; oldVal <= res; oldVal++) {
				view.get("#exportProgressBar").set("value", res/10);
			}
		}
		oldVal = res;
		if (res == 999) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#impCloseBtn").set("disabled",false);
			view.get("#progressLabel").set("text","导入失败！");
		}
		if (res == 1000) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#impCloseBtn").set("disabled",false);
			view.get("#progressLabel").set("text","导入完成！");
		}
	});
};

var progressJob = setInterval(getProgress, 500);

