FloatPanel.ClientEvent:
var data = this.get("#viewSelectCustomer.userData");
if (data != null) {
	var entity = this.get("#dsGenericTemplate").getData("#");
	
	var delivers=view.get("#dsAllDeliver.data");
	var deliverName="";
	delivers.each(function(item){
		if(entity.get("deliver")==item.get("code")){
			deliverName=item.get("codeName");
		}
	});
	
    entity.set({
        customerId:data.get("customerid"),
		customerName:data.get("customername"),
		templateName:data.get("customername")+'-'+deliverName+'-报价'
    });
}

