UploadAction.ClientEvent:
$("#d_progressLabel2").show();
$("#d_exportProgressBar2").show();
view.get("#progressLabel2").set("text", "正在处理数据，请耐心等待...");

var action = view.get("#getProgess2");
var setaction=view.get("#setProgress2");
var oldVal = 0;
var getProgress = function() {
	action.execute(function (res) {	
		oldVal = res;
		var mes="";
		if(oldVal==0){
			view.get("#exportProgressBar2").set("value", 10);
			mes="开始处理";
		}
		else if(oldVal==1)
		{
			view.get("#exportProgressBar2").set("value", 20);
			mes="验证模板";
		}
		else if(oldVal==2)
		{
			view.get("#exportProgressBar2").set("value", 30);
			mes="读取Excel";
		}
		else if(oldVal==3)
		{
			view.get("#exportProgressBar2").set("value", 40);
			mes="读取系统数据";
		}
		else if(oldVal==4)
		{
			view.get("#exportProgressBar2").set("value", 80);
			mes="开始比较数据";
		}
		else if(oldVal==5)
		{
			clearInterval(progressJob);
			//设置为 0
			setaction.execute(function (res) {  });
			view.get("#exportProgressBar2").set("value", 100);
			mes="比较结束";
		}
		else if(oldVal==6)
		{
			clearInterval(progressJob);
			//设置为 0
			setaction.execute(function (res) {  });
			view.get("#exportProgressBar2").set("value", 99);
			mes="比较异常";
		}
		else
		{
			view.get("#exportProgressBar2").set("value", 0);
			mes="";
		}	
		view.get("#progressLabel2").set("text",mes);
	});
};
var progressJob = setInterval(getProgress, 1000);

view.get("#impBtn4").set("disabled",false);
view.get("#impCloseBtn2").set("disabled",false);
var data= arg.returnValue;
if(data.error){
	dorado.MessageBox.alert(data.error);
	return;
}
if(data.succ){
	view.get("#compareMsgDialog").show();
	view.get("#exportProgressBar2").set("value", 100);
	view.get("#progressLabel2").set("text","比较成功！");
	var dataSet=view.get("#dsimportCompareVo");
    dataSet.setData(data.succ);
	return;
}
