DataSetDropDown.ClientEvent:
var data = this.get("#dsBmsQuoteDispatchDetailVo").getData("#");
data.set({
	//provinceName:arg.selectedValue.get("province"),
	//provinceId:arg.selectedValue.get("provincecode")
	provinceId:arg.selectedValue.get("province"),
});

//data.set("cityName","");
data.set("cityId","");
//data.set("areaName","");
data.set("areaId","");

arg.processDefault = false;

var data = this.get("#dsBmsQuoteDispatchDetailVo").getData("#");
var provinceId = data.get("provinceId");
//var provinceName = data.get("provinceName");

if(provinceId){
	var parameter={
		province:provinceId
	};
	view.get("#dsCities").set("parameter",parameter).flushAsync();
}else{
	this.get("#dsCities").clear();
}

var data = this.get("#dsBmsQuoteDispatchDetailVo").getData("#");
data.set({
	//cityName:arg.selectedValue.get("city"),
	//cityId:arg.selectedValue.get("citycode")
	cityId:arg.selectedValue.get("city"),
});

//data.set("areaName","");
data.set("areaId","");
arg.processDefault = false;

var data = this.get("#dsBmsQuoteDispatchDetailVo").getData("#");
var provinceId = data.get("provinceId");
var cityId = data.get("cityId");

if(cityId){
	var parameter={
		province:provinceId,city:cityId
	};
	view.get("#dsDistricts").set("parameter",parameter).flushAsync();
}else{
	this.get("#dsDistricts").clear();
}

var data = this.get("#dsBmsQuoteDispatchDetailVo").getData("#");
data.set({
	//areaName:arg.selectedValue.get("district"),
	//areaId:arg.selectedValue.get("districtCode")
	areaId:arg.selectedValue.get("district")
});
arg.processDefault = false;

ToolBarButton.ClientEvent:
var tmpDataSet=view.get("#dsGenericTemplate");
var tmpFormCondition=view.get("#formCondition");

var cre=tmpFormCondition.get("entity").get("startTime");
var end=tmpFormCondition.get("entity").get("endTime");
if(!isNullOrEmpty(cre) && !isNullOrEmpty(end)){
 	if(cre.getTime()>end.getTime()){
		dorado.MessageBox.alert("起始时间不能大于结束时间！");
		return;
	}
}

tmpDataSet.set("parameter", tmpFormCondition.get("entity")).flushAsync();

DataGrid.ClientEvent:
var data = view.get("#dsGenericTemplate").getData();
with (view.get("#dsBmsQuoteDispatchDetailVo")){
	//定义查询条件json结构
	var queryJson={
		templateCode:data.current.get("templateCode")
	};
	set("parameter", queryJson);
	flushAsync();
}

ToolBarButton.ClientEvent:
var data = view.get("#dsGenericTemplate").getData();
var tmpFormCondition=view.get("#dsPriceCondition").getData();
var startWarehouseId=tmpFormCondition.current.get("startWarehouseId");
var provinceId=tmpFormCondition.current.get("provinceId");
var cityId=tmpFormCondition.current.get("cityId");
var areaId=tmpFormCondition.current.get("areaId");

with (view.get("#dsBmsQuoteDispatchDetailVo")){
	//定义查询条件json结构
	var queryJson={
		templateCode:data.current.get("templateCode"),
		startWarehouseId:startWarehouseId,
		provinceId:provinceId,
		cityId:cityId,
		areaId:areaId
	};
	set("parameter", queryJson);
	flushAsync();
};


