var entity=view.get("#dsQuotaList.data").current;
//获取商家id
var customerName=entity.get("customerName");
//获取费用科目
var subjectId=entity.get("subjectId");

if("wh_b2c_work"==subjectId){
	//订单操作费
	view.get("#orderTab").set("visible",true);
	view.get("#storageTab").set("visible",false);
	view.get("#inStorageTab").set("visible",false);
	view.get("#returnSaleTab").set("visible",false);
	view.get("#returnStockTab").set("visible",false);
	view.get("#b2bOutTab").set("visible",false);
	view.get("#materailTab").set("visible",false);
	
	view.get("#dsOutStock").getData().insert();
	view.get("#order").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();	
}else if("wh_product_storage"==subjectId||"wh_product_pallet_storage"==subjectId){
	//商品存储费
	view.get("#storageTab").set("visible",true);
	view.get("#orderTab").set("visible",false);
	view.get("#inStorageTab").set("visible",false);
	view.get("#returnSaleTab").set("visible",false);
	view.get("#returnStockTab").set("visible",false);
	view.get("#b2bOutTab").set("visible",false);
	view.get("#materailTab").set("visible",false);
	
	view.get("#dsRepertory").getData().insert();
	view.get("#storage").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();
}else if("wh_instock_service"==subjectId || "wh_instock_work"==subjectId || "wh_b2c_handwork"==subjectId){
	//入仓费
	view.get("#inStorageTab").set("visible",true);
	view.get("#orderTab").set("visible",false);
	view.get("#storageTab").set("visible",false);
	view.get("#returnSaleTab").set("visible",false);
	view.get("#returnStockTab").set("visible",false);
	view.get("#b2bOutTab").set("visible",false);
	view.get("#materailTab").set("visible",false);
	
	view.get("#dsMasterList").getData().insert();
	view.get("#inStorage").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();
}else if("wh_return_storage"==subjectId){
	//退货费
	view.get("#returnSaleTab").set("visible",true);
	view.get("#orderTab").set("visible",false);
	view.get("#storageTab").set("visible",false);
	view.get("#inStorageTab").set("visible",false);
	view.get("#returnStockTab").set("visible",false);
	view.get("#b2bOutTab").set("visible",false);
	view.get("#materailTab").set("visible",false);
	
	view.get("#dsReturnList").getData().insert();
	view.get("#returnSales").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();
}else if("wh_product_out"==subjectId){
	//退仓费
	view.get("#returnStockTab").set("visible",true);
	view.get("#orderTab").set("visible",false);
	view.get("#storageTab").set("visible",false);
	view.get("#inStorageTab").set("visible",false);
	view.get("#returnSaleTab").set("visible",false);
	view.get("#b2bOutTab").set("visible",false);
	view.get("#materailTab").set("visible",false);
	
	view.get("#dsReturnStock").getData().insert();
	view.get("#returnStock").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();
}else if("wh_b2b_work"==subjectId){
	//B2B出库费
	view.get("#b2bOutTab").set("visible",true);
	view.get("#orderTab").set("visible",false);
	view.get("#storageTab").set("visible",false);
	view.get("#inStorageTab").set("visible",false);
	view.get("#returnSaleTab").set("visible",false);
	view.get("#returnStockTab").set("visible",false);
	view.get("#materailTab").set("visible",false);
	
	view.get("#dsB2bOut").getData().insert();
	view.get("#b2bOut").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();
}else if("wh_material_storage"==subjectId){
	//耗材存储费
	view.get("#materailTab").set("visible",true);
	view.get("#orderTab").set("visible",false);
	view.get("#storageTab").set("visible",false);
	view.get("#inStorageTab").set("visible",false);
	view.get("#returnSaleTab").set("visible",false);
	view.get("#returnStockTab").set("visible",false);
	view.get("#b2bOutTab").set("visible",false);
	
	view.get("#dsMaterial").getData().insert();
	view.get("#materailStock").get("entity").set("customerName",customerName);
	view.get("#calculateDialog").show();
}

