FloatPanel.ClientEvent:
var data = this.get("#viewSelectRule.userData");
if (data != null) {
	var entity = this.get("#dsQuotaList").getData("#");
    entity.set({
        ruleNo:data.get("quotationNo"),
		ruleName:data.get("quotationName")
    });
}

DataSetDropDown.ClientEvent:
var groupCode = "receive_wh_base_quo_subject";

var subDropDown = view.get("#subjectIdDataSetDropDown");
subDropDown.set("dataSet","dsSubjectId");
var parameter={
	groupCode:groupCode
};
view.get("#dsSubjectId").set("parameter",parameter).flushAsync();

var data = this.get("#dsQuotaList").getData("#");
data.set({
	subjectName:arg.selectedValue.get("subjectName"),
	subjectId:arg.selectedValue.get("subjectCode")
});
arg.processDefault = false;
