var dataPrice = view.get("#dataSetTransportLine.data").current;
if(dataPrice){
	//获取此时的报价
	var dataSetTransportTemplate=view.get("#dataSetTransportTemplate.data").current;
	var customerId=dataSetTransportTemplate.get("customerId");
	var customerName=dataSetTransportTemplate.get("customerName");
	view.get("#dsBizGanxianWayBillEntity").getData().insert();
	view.get("#dsBizGanxianWayBillEntity.data").current.set("customerName",customerName);
	view.get("#dsBizGanxianWayBillEntity.data").current.set("customerId",customerId);
	
	//获取此时的报价类型
	var price=view.get("#dataSetTransportLine.data").current;
	var bizTypeCode=price.get("transportTypeCode");
	if("CJ"==bizTypeCode){
		view.get("#CJTab").set("visible",true);
		view.get("#TCTab").set("visible",false);
		view.get("#DSZLTab").set("visible",false);
		view.get("#HXDTab").set("visible",false);
	}else if("TC"==bizTypeCode){
		view.get("#TCTab").set("visible",true);
		view.get("#CJTab").set("visible",false);
		view.get("#DSZLTab").set("visible",false);
		view.get("#HXDTab").set("visible",false);
	}else if("DSZL"==bizTypeCode){
		view.get("#DSZLTab").set("visible",true);
		view.get("#CJTab").set("visible",false);
		view.get("#TCTab").set("visible",false);
		view.get("#HXDTab").set("visible",false);
	}else if("HXD"==bizTypeCode){
		view.get("#HXDTab").set("visible",true);
		view.get("#CJTab").set("visible",false);
		view.get("#TCTab").set("visible",false);
		view.get("#DSZLTab").set("visible",false);
		
	}
	
	view.get("#calculateDialog").show();	
}else{
	dorado.MessageBox.alert("请首先选择需要试算的报价!");
	return;
}

