Button.ClientEvent:
view.id("saveTransportLineAction").execute(
	function(result){
		if("succ" == result){
			dorado.MessageBox.alert("操作成功！");
			view.id("saveTransportLineDialog").hide();
			view.get("#dataSetTransportLine").flushAsync();
		}else{
			var spiltArray = new Array();
			var spiltResult = "";
			if(result.indexOf("!") != -1){
				spiltArray = result.split("!");
			}
			for(var i=0;i < spiltArray.length-1; i++){
				spiltResult += spiltArray[i];
				spiltResult += "!\n";
			}
			dorado.MessageBox.alert(spiltResult);
			return;
		}
	}
);

//定义一个函数，判断参数是否为空
function isNullOrEmpty(e) {
    if (e == null || e == undefined || e == "") {
        return true;
    } else {
        return false;
    }
}

