$("#d_fileName input").val(fname);
view.get("#importRangeBtn2").set("disabled",true);
view.get("#importRangeCloseBtn").set("disabled",true);

var entity = view.get("#dataSetTransportLine.data").current;
var id = entity.get("id");
var parameter={id:id};
self.set("parameter",parameter);

$("#d_progressRangeLabel").show();
$("#d_importRangeProgressBar").show();
view.get("#progressRangeLabel").set("text", "请不要关闭当前页面，系统正在处理数据，请耐心等待...");
view.get("#importRangeProgressBar").set("value", 0);
var action = view.get("#getRangeProgress");
var setAction = view.get("#setRangeProgress");
var oldVal = 0;
var getRangeProgress = function() {
	action.execute(function (res) {
		
		if (oldVal != res) {
			for (oldVal; oldVal <= res; oldVal++) {
				view.get("#importRangeProgressBar").set("value", res/10);
			}
		}
		oldVal = res;
		if (res == 999) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#importRangeCloseBtn").set("disabled",false);
			view.get("#progressRangeLabel").set("text","导入失败！");
		}
		if (res == 1000) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#importRangeCloseBtn").set("disabled",false);
			view.get("#progressRangeLabel").set("text","导入完成！");
		}
	});
};

var progressJob = setInterval(getRangeProgress, 500);

