	var datas = view.get("#dataSetTransportLineRange.data");
	datas.insert();
	datas.current.set("lineId",currentData.get("id"));
	view.get("#saveTransportLineRangeDialog").show();
}else{
    dorado.MessageBox.alert("请先选择一条运输路线!");
}

