	dorado.MessageBox.alert("请选择需要修改的记录!");
}

var datas = view.get("#dataSetTransportLineRange.data");
var currentData = datas.current;
if(currentData){
	dorado.MessageBox.confirm("是否确定要删除此条记录?",function(){
		view.get("#removeLineRange").execute(
		    function(result){
				if(result == "SUCCESS"){
					dorado.MessageBox.alert("删除成功");
					var queryJson={
						lineId:currentData.get("lineId")
					};
					with (view.get("#dataSetTransportLineRange")){
						set("parameter", queryJson);
						flushAsync();
					}
				}else{
					dorado.MessageBox.alert(result);
				}
			}
		);
	});
}else{
	dorado.MessageBox.alert("请先选择要删除的记录!");
}

