	delFlag: "0",
	templateTypeCode:"TRANSPORT_FEE"
});

DataSetDropDown.ClientEvent:
var data = this.get("#dataSetTransportLine").getData("#");
//把名字赋给ID
data.set({
	fromProvinceId:arg.selectedValue.get("province")
});

data.set("fromCityName","");
data.set("fromCityId","");

data.set("fromDistrictName","");
data.set("fromDistrictId","");


arg.processDefault = false;

var data = this.get("#dataSetTransportLine").getData("#");
data.set({
	fromCityId: arg.selectedValue.get("city")
});

data.set("fromDistrictName","");
data.set("fromDistrictId","");
arg.processDefault = false;

var data = this.get("#dataSetTransportLine").getData("#");
var fromProvinceName = data.get("fromProvinceId");

if(fromProvinceName){
	var parameter={
		province:fromProvinceName
	};
	view.get("#dsCities").set("parameter",parameter).flushAsync();
}else{
	this.get("#dsCities").clear();
}

var data = this.get("#dataSetTransportLine").getData("#");
var fromProvinceName = data.get("fromProvinceId");
var fromCityName = data.get("fromCityId");
if(fromProvinceName && fromCityName){
	var parameter={
		province:fromProvinceName,city:fromCityName
	};
	view.get("#dsDistricts").set("parameter",parameter).flushAsync();
}else{
	this.get("#dsDistricts").clear();
}

var data = this.get("#dataSetTransportLine").getData("#");
data.set({
	fromDistrictId:arg.selectedValue.get("district")
});
arg.processDefault = false;

