//查看当前Active的Tab
var currentTab = view.get("#transportTemplateTab").get("currentTab");

//如果currentTab已经切换到lineTab,则将lineRangeTab禁用,直到下次双击运输路线数据从新激活lineRangeTab.
if(currentTab){
	var currentTabId = currentTab._id;
	if(currentTabId.trim() == "lineRangeTab"){
		console.log("Current Tab Id:"+ currentTabId);
		//view.get("#lineRangeTab").set("disabled",true);
	}
}

