var typeDatas = view.get("#dataSetTransportTemplate.data");
var currentData = typeDatas.current;
if(!currentData){
	dorado.MessageBox.alert("请先选择运输报价模板再查询!");
}
var lineDataSet=view.get("#dataSetTransportLine");

var queryLineForm=view.get("#queryFormTransportLine");
queryLineForm.get("entity").set("delFlag","0");
queryLineForm.get("entity").set("templateId",currentData.get("id"));
lineDataSet.set("parameter", queryLineForm.get("entity")).flushAsync();

