DataGrid.ClientEvent:
var data = view.get("#dataSetTransportLine.data");
var lineId=data.current.get("id");

with (view.get("#dataSetTransportLineRange")){
	//定义查询条件json结构
	var queryJson={
		lineId:lineId,
		delFlag:"0"
	};
	set("parameter", queryJson);
	flushAsync();
	
	view.get("#lineRangeTab").set("caption",data.current.get("transportLineNo")+" 梯度报价");
}

/*
//原始代码：
var data=view.get("#dataSetTransportLine.data");
if(data.current){
	view.get("#editTransportLineDialog").show();
}else{
	dorado.MessageBox.alert("请选择需要修改的记录!");
}
*/

var data = view.get("#dataSetTransportLine.data");
var lineId=data.current.get("id");

with (view.get("#dataSetTransportLineRange")){
	//定义查询条件json结构
	var queryJson={
		lineId:lineId,
		delFlag:"0"
	};
	set("parameter", queryJson);
	flushAsync();
	
	
	view.get("#lineRangeTab").set("disabled",false);
	view.get("#lineRangeTab").set("caption",data.current.get("transportLineNo")+" 梯度报价");
	view.get("#transportTemplateTab").set("currentTab",view.get("#lineRangeTab"));
}

FloatPanel.ClientEvent:
var data = this.get("#viewSelectCarrier1.userData");
if (data != null) {
	var entity = this.get("#dataSetTransportLine").getData("#");
    entity.set({
        carrierCode:data.get("deliverid"),
		carrierName:data.get("delivername")
    });
}

DataGrid.ClientEvent:
var data=view.get("#dataSetTransportLineRange.data");
if(data.current){
	view.get("#editTransportLineRangeDialog").show();
}else{
	dorado.MessageBox.alert("请选择需要修改的记录!");
}

Action.ClientEvent:
var tmpSelectData = this.id("dataSetTransportTemplate").getData("#");
if (tmpSelectData != null) {
	this.get("parent").set("userData", tmpSelectData); // 提交选择的记录
	this.get("parent").get("parent").hide(); // 关闭当前按子页面
}

FloatPanel.ClientEvent:
var data = this.get("#viewSelectCustomer1.userData");
if (data != null) {
	var entity = this.get("#dataSetCondition").getData("#");
    entity.set({
        customerId:data.get("customerid"),
    });
}
