ToolBarButton.ClientEvent:
var templateDataSet=view.get("#dataSetTransportTemplate");
var queryEntity=view.get("#queryTransportTemplateForm").get("entity");
var cre=queryEntity.get("startTime");
var end=queryEntity.get("endTime");
if(!isNullOrEmpty(cre) && !isNullOrEmpty(end)){
 	if(cre.getTime()>end.getTime()){
		dorado.MessageBox.alert("起始时间不能大于结束时间！");
		return;
	}
}
queryEntity.set("templateTypeCode","ts_value_add_subject");
templateDataSet.set("parameter", queryEntity).flushAsync();

//清空valueAdded
var valueAddedData = view.get("#dataSetTransportValueAdded.data");
valueAddedData.clear();

//Template中插入一条空行,如果取消时记得同时取消
var datas = view.get("#dataSetTransportTemplate.data");
datas.insert();

//打开运输模板新增编辑窗口时,设置里面的Form中的默认值
var editTemplateFormEntity = view.get("#editTransportTemplateForm.entity");
editTemplateFormEntity.set("templateTypeCode","ts_value_add_subject");

//然后弹出新增编辑窗口
view.get("#editTransportTemplateDialog").show();

var data=view.get("#dataSetTransportTemplate.data");
if(data.current){
	//打开运输模板新增编辑窗口时,设置里面的Form中的默认值
	var editTemplateFormEntity = view.get("#editTransportTemplateForm.entity");
	editTemplateFormEntity.set("templateTypeCode","ts_value_add_subject");
	
	//然后弹出新增编辑窗口
	view.get("#editTransportTemplateDialog").show();
}else{
	dorado.MessageBox.alert("请先选择需要修改的记录!");
}

var datas = view.get("#dataSetTransportTemplate.data");
var currentData = datas.current;
if(currentData){
	dorado.MessageBox.confirm("是否确定要删除此条【运输费报价模板】记录?",function(){
		view.get("#removeTransportTemplateAction").execute(
		    function(result){
				if(result == "SUCCESS"){
					dorado.MessageBox.alert("删除成功");
					var param = view.get("#dataSetCondition.data").current;
					param.set("delFlag","0");
					param.set("templateTypeCode","ts_value_add_subject");
					with (view.get("#dataSetTransportTemplate")){
						set("parameter", param);
						flushAsync();
						//清空valueAdded显示
						var valueAddedData = view.get("#dataSetTransportValueAdded.data");
						valueAddedData.clear();
					}
				}else{
					dorado.MessageBox.alert(result);
				}
			}
		);
	});
}else{
	dorado.MessageBox.alert("请先选择要删除的记录!");
}

var datas = view.get("#dataSetTransportTemplate.data");
var currentData = datas.current;
if(currentData){
	if(!isNullOrEmpty(currentData.get("templateType")) && "S" == currentData.get("templateType")){
		dorado.MessageBox.alert("标准报价模版不允许复制!");
		return;
	}
	dorado.MessageBox.confirm("是否确定要复制此模板?",function(){
		view.get("#copyTransportTemplateValueAddedAction").execute({
			callback:function(success){
				var param = view.get("#dataSetCondition.data").current;
				param.set("delFlag","0");
				param.set("templateTypeCode","ts_value_add_subject");
				with (view.get("#dataSetTransportTemplate")){
					flushAsync();
					//清空valueAdded显示
					var valueAddedData = view.get("#dataSetTransportValueAdded.data");
					valueAddedData.clear();
				}
			}
		});
	});
}else{
	dorado.MessageBox.alert("请先选择需要复制的运输模板!");
}

var data=view.get("#dataSetTransportTemplate.data");
