Button.ClientEvent:
view.id("updateTransportTemplateAction").execute(
	function(result){
		if(!isNullOrEmpty(result)){
			dorado.MessageBox.alert(result);
		}
		
		view.id("editTransportTemplateDialog").hide();
		
		var tmpDataSet=view.get("#dataSetTransportTemplate");
		var tmpFormCondition=view.get("#queryTransportTemplateForm");
		var tmpFormCondition=view.get("#queryTransportTemplateForm");
		tmpFormCondition.get("entity").set("delFlag","0");
		tmpFormCondition.get("entity").set("templateTypeCode","ts_value_add_subject");
		
		tmpDataSet.set("parameter", tmpFormCondition.get("entity")).flushAsync();
	}
);

dorado.MessageBox.confirm("是否确定取消此操作？",
	function(){
		view.id("dataSetTransportTemplate").getData("#").cancel();
		view.id("editTransportTemplateDialog").hide();
	}
);

