UploadAction.ClientEvent:
view.get("#importBtn2").set("disabled",false);
view.get("#importCloseBtn").set("disabled",false);

//导入Excel之后，在这里拿到上传成功之后的返回值 例如java方法里返回Map值这里获得数据后会映射成json
//注意这里
var data= arg.returnValue;
if(data.error!=null){
	 var dataSet=view.get("#dsErrorMessageVo");
     dataSet.setData(data.error);
     view.get("#errorProductMsgDialog").show();
	 view.get("#progressLabel").set("text","导入失败！");
	 return;
}
if(data.succ!=null){
	view.get("#progressLabel").set("text","导入成功！");
	
	var data = view.get("#dataSetTransportTemplate").getData();
	with (view.get("#dataSetTransportValueAdded")){
		//定义查询条件json结构
		var queryJson={
			templateId:data.current.get("id")
		};
		set("parameter", queryJson);
		flushAsync();
	}
}

var fname = arg.file.name;
$("#d_fileName input").val(fname);
view.get("#importBtn2").set("disabled",true);
view.get("#importCloseBtn").set("disabled",true);

var entity = view.get("#dataSetTransportTemplate.data").current;
var id = entity.get("id");
var parameter={id:id};
self.set("parameter",parameter);

$("#d_progressLabel").show();
$("#d_importProgressBar").show();
view.get("#progressLabel").set("text", "请不要关闭当前页面，系统正在处理数据，请耐心等待...");
view.get("#importProgressBar").set("value", 0);
var action = view.get("#getProgress");
var setAction = view.get("#setProgress");
var oldVal = 0;
var getProgress = function() {
	action.execute(function (res) {
		
		if (oldVal != res) {
			for (oldVal; oldVal <= res; oldVal++) {
				view.get("#importProgressBar").set("value", res/10);
			}
		}
		oldVal = res;
		if (res == 999) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#importCloseBtn").set("disabled",false);
			view.get("#progressLabel").set("text","导入失败！");
		}
		if (res == 1000) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#importCloseBtn").set("disabled",false);
			view.get("#progressLabel").set("text","导入完成！");
		}
	});
};

var progressJob = setInterval(getProgress, 500);

